								  and cxpnotasd.procode = c.procode
			Where cxpnotasd.factura = pFactura
			and cxpnotasd.tipo = pTipo 
			and cxpnotasd.procode = pProcode),0);
	/* Fin Bosco modificado 28/04/2013 */

    Return vRecibos + vNotasD;
