)
BEGIN
    -- Autor: Bosco Garita Azofeifa
    
    
    
    Declare vEmpresa varchar(60);
    
    Select Empresa from config into vEmpresa;
    
    
    If pOrden is null or pOrden not between 0 and 2 then
        Set pOrden = 0;
